\);

async function main\(\) {
  console.log\('üì• Copiando posts do Docker ‚Üí Supabase\\n'\);

  // 1. Buscar posts do Docker
  const cmd = `docker exec -i nfc-postgres psql -U nfc -d nfc_admin -c "SELECT id, content, \\\\\\"arenaId\\\\\\", \\\\\\"userId\\\\\\", \\\\\\"isAIResponse\\\\\\", \\\\\\"isPublished\\\\\\", \\\\\\"isApproved\\\\\\", \\\\\\"createdAt\\\\\\", \\\\\\"updatedAt\\\\\\" FROM \\\\\\"Post\\\\\\" ORDER BY \\\\\\"createdAt\\\\\\";"`

  const result = execSync\(cmd, { encoding: 'utf-8' }\);
  const lines = result.split\('\\n'\).filter\(l => l.trim\(\) && !l.includes\('id | content'\)\);

  console.log\(`üìä Posts no Docker: ${lines.length}\\n`\);

